<?phpnamespace ESG\Panther\Dao;class ExtCategoryMappingDao extends BaseDao{    private $tableName = "ext_category_mapping";    private $voClassName = "ExtCategoryMappingVo";    private $debug = 0;    public function __construct()    {        parent::__construct();    }    public function getVoClassname()    {        return $this->voClassName;    }    public function getTableName()    {        return $this->tableName;    }    public function getGoogleCategoryMappingList($where = array(), $option = array(), $classname = "GoogleCategoryMappingDto")    {        $this->db->from("category c");        $this->db->join("ext_category_mapping ext_c", "ext_c.category_id = c.id and ext_c.ext_party='GOOGLEBASE'", "LEFT");        $this->db->join("external_category ext_c_2", "ext_c_2.id = ext_c.ext_id", "LEFT");        $where['c.hidden'] = 0;        $where['c.status'] = 1;        return $this->commonGetList($classname, $where, $option, 'c.id as category_id, c.name, ext_c.ext_id, ext_c.country_id,  ext_c_2.ext_name as google_category_name');    }    public function getCategoryCombination($where = array(), $option = array(), $classname = "CategoryVo")    {        $this->db->from("category c");        $this->db->join("category c1", "c.parent_cat_id = c1.id", "LEFT");        $this->db->join("category c2", "c1.parent_cat_id = c2.id", "LEFT");        $where['c.hidden'] = 0;        $where['c.status'] = 1;        $where['c1.hidden'] = 0;        $where['c1.status'] = 1;        $where['c2.hidden'] = 0;        $where['c2.status'] = 1;        return $this->commonGetList($classname, $where, $option, "c.id as id, concat(c2.name, '->', c1.name,'->', c.name) as name");    }}